<?php if ($currentPage>1) { ?><a href="<?php echo _url('#', array('page_'.$idlist=>1)); ?>" ><img src="<?php echo _resource('img/pager-first.png'); ?>" alt="&laquo;" /></a><?php } ?>
<?php if ($currentPage>1) { ?><a href="<?php echo _url('#', array('page_'.$idlist=>($currentPage-1))); ?>" ><img src="<?php echo _resource('img/pager-previous.png'); ?>" alt="&lt;" /></a><?php } ?>
<?php echo $currentPage.'/'.$nbpages; ?>
<?php if ($currentPage < $nbpages) { ?><a href="<?php echo _url('#', array('page_'.$idlist=>($currentPage+1))); ?>"><img src="<?php echo _resource('img/pager-next.png'); ?>" alt="&gt;" /></a><?php } ?>
<?php if ($currentPage < $nbpages) { ?><a href="<?php echo _url('#', array('page_'.$idlist=>($nbpages))); ?>"><img src="<?php echo _resource('img/pager-last.png'); ?>" alt="&raquo;" /></a><?php }